import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Currency Calculator';
  description = "hey this is a currency calculator"
  val = 0;
  total = 0


  // USD TO INR Rate = 80
  // USD TO GBP Rate = 0.82
  // USD TO CAD RATE = 1.30
  // USD TO JPY RATE = 130
  // USD TO USD RATE = 1

 process(){
  var curr1:String = (<HTMLInputElement>(document.getElementById('curr1'))).value; 
  var curr2: String = (<HTMLInputElement>(document.getElementById('curr2'))).value; 
  var value = (<HTMLInputElement>(document.getElementById('number'))).value;
  console.log(value)
    switch (curr1) {
      case "INR":

        break;

      default:
        break;
    }
  }
}
<h1>{{title}}</h1>
<div class="currHolder">
    <p>From</p>
    <select name="curr1" id="curr1">
        <option value="INR">INDIAN RUPEE</option>
        <option value="USD">US DOLLAR</option>
        <option value="GBP">BRITISH POUND</option>
        <option value="CAD">CANADIAN DOLLAR</option>
        <option value="JPY">JAPANESE YEN</option>
    </select>
    <p>To</p>
    <select name="curr2" id="curr2">
        <option value="INR">INDIAN RUPEE</option>
        <option value="USD">US DOLLAR</option>
        <option value="GBP">BRITISH POUND</option>
        <option value="CAD">CANADIAN DOLLAR</option>
        <option value="JPY">JAPANESE YEN</option>
    </select>   
</div>
<div class="form">
    <input type="number" name="number" id="number">
    <input type="button" value="Submit" (click)="process()">

    <p>{{total}}</p>
</div>
